window.onload=function(){document.body.classList.add("loaded_hiding"),window.setTimeout(function(){document.body.classList.add("loaded"),document.body.classList.remove("loaded_hiding")},500)};const burger=document.querySelector(".header__burger-wrapper"),burgerSpan=document.querySelector(".header__burger"),headerMenu=document.querySelector(".header__nav");burger.addEventListener("click",()=>{burgerSpan.classList.toggle("burger-center"),setTimeout(()=>{burgerSpan.classList.toggle("active"),document.body.classList.toggle("active"),headerMenu.classList.toggle("active")},500)});const successModal=document.querySelector(".success-modal"),modalClose=document.querySelectorAll(".modal-dialog__close");function showModal(e){e.classList.remove("hideModal"),e.classList.add("showModal"),callModal(e),document.body.style.overflow="hidden"}function hideModal(e){e.classList.remove("showModal"),e.classList.add("hideModal"),document.body.style.overflow=""}function callModal(e){e.addEventListener("click",o=>{o.target===e&&hideModal(e)}),modalClose.forEach(o=>{o.addEventListener("click",()=>hideModal(e))}),document.addEventListener("keydown",o=>{"Escape"==o.code&&e.classList.contains("showModal")&&hideModal(e)})}